import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { FaSignOutAlt, FaTicketAlt, FaUser } from "react-icons/fa";
import "./NavBar.css";

const NavBar = () => {
    const [usuario, setUsuario] = useState(null);

    // Simulando la autenticación (esto se reemplazaría con datos reales)
    useEffect(() => {
        // Simulación de usuario autenticado (reemplazar con API o localStorage)
        const usuarioAutenticado = {
            nombre: "García", // Este dato podría venir de una API o localStorage
            autenticado: true
        };
        if (usuarioAutenticado.autenticado) {
            setUsuario(usuarioAutenticado);
        }
    }, []);

    // Función para cerrar sesión
    const handleLogout = () => {
        setUsuario(null); // Simula el cierre de sesión (debería eliminarse del almacenamiento local o backend)
        alert("Has cerrado sesión correctamente."); // Mensaje de confirmación (puedes reemplazarlo con una redirección)
    };

    return (
        <Navbar bg="primary" variant="dark" expand="lg" className="mb-4">
            <Container>
                <Navbar.Brand href="#home">
                    <img 
                        src="/images/logo.png" 
                        alt="Logo" 
                        className="custom-logo"
                    />
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ms-auto">
                        <Nav.Link href="#home">Inicio</Nav.Link>
                        <Nav.Link href="#about">Sobre Nosotros</Nav.Link>
                        <Nav.Link href="#contact">Contacto</Nav.Link>

                        {usuario ? (
                            <NavDropdown 
                                title={
                                    <>
                                        <FaUser className="me-2" /> Bienvenido, Sr./a {usuario.nombre}
                                    </>
                                } 
                                id="user-dropdown"
                            >
                                <NavDropdown.Item href="#entradas">
                                    <FaTicketAlt className="me-2" /> Mis Entradas
                                </NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item onClick={handleLogout}>
                                    <FaSignOutAlt className="me-2" /> Cerrar Sesión
                                </NavDropdown.Item>
                            </NavDropdown>
                        ) : (
                            <Nav.Link href="#login">
                                <FaUser className="me-2" /> Iniciar Sesión
                            </Nav.Link>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default NavBar;
