import axios from "axios"; // Para hacer la petición al backend
import React, { useEffect, useState } from "react";
import { Alert, Button, Container, Form, Modal, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { FaSignOutAlt, FaTicketAlt, FaUser } from "react-icons/fa";
import "./NavBar.css";

const NavBar = () => {
    const [usuario, setUsuario] = useState(null);
    const [showLogin, setShowLogin] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    useEffect(() => {
        const usuarioGuardado = localStorage.getItem("usuario");
        if (usuarioGuardado) {
            setUsuario(JSON.parse(usuarioGuardado));
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(""); // Limpia errores previos

        try {
            const response = await axios.post("http://127.0.0.1:5000/login", { email, password });
            if (response.data.success) {
                const usuarioData = { nombre: response.data.nombre };
                setUsuario(usuarioData);
                localStorage.setItem("usuario", JSON.stringify(usuarioData)); // Guarda sesión en localStorage
                setShowLogin(false);
                setEmail("");
                setPassword("");
            }
        } catch (err) {
            setError("Correo o contraseña incorrectos.");
        }
    };

    const handleLogout = () => {
        setUsuario(null);
        localStorage.removeItem("usuario"); // Elimina sesión
        alert("Has cerrado sesión correctamente.");
    };

    return (
        <>
            <Navbar bg="primary" variant="dark" expand="lg" className="mb-4">
                <Container>
                    <Navbar.Brand href="#home">
                        <img src="/images/logo.png" alt="Logo" className="custom-logo" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="ms-auto">
                            <Nav.Link href="#home">Inicio</Nav.Link>
                            <Nav.Link href="#about">Sobre Nosotros</Nav.Link>
                            <Nav.Link href="#contact">Contacto</Nav.Link>

                            {usuario ? (
                                <NavDropdown title={<><FaUser /> ¡Hola, {usuario.nombre}!</>} id="user-dropdown">
                                    <NavDropdown.Item href="#entradas">
                                        <FaTicketAlt className="me-2" /> Mis Entradas
                                    </NavDropdown.Item>
                                    <NavDropdown.Divider />
                                    <NavDropdown.Item onClick={handleLogout}>
                                        <FaSignOutAlt className="me-2" /> Cerrar Sesión
                                    </NavDropdown.Item>
                                </NavDropdown>
                            ) : (
                                <Nav.Link onClick={() => setShowLogin(true)}>
                                    <FaUser /> Iniciar Sesión
                                </Nav.Link>
                            )}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            {/* Modal de Inicio de Sesión */}
            <Modal show={showLogin} onHide={() => setShowLogin(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Iniciar Sesión</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form onSubmit={handleLogin}>
                        <Form.Group className="mb-3">
                            <Form.Label>Correo Electrónico</Form.Label>
                            <Form.Control
                                type="email"
                                placeholder="Ingrese su correo"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contraseña</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Ingrese su contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="w-100">
                            Iniciar Sesión
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default NavBar;
