import axios from "axios";
import React, { useEffect, useState } from "react";
import { Alert, Button, Container, Form, Modal, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { FaSignOutAlt, FaTicketAlt, FaUser } from "react-icons/fa";
import "./NavBar.css";

const NavBar = () => {
    const [usuario, setUsuario] = useState(null);
    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);
    const [user, setUser] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [profile, setProfile] = useState(null);
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    useEffect(() => {
        const usuarioGuardado = localStorage.getItem("usuario");
        if (usuarioGuardado) {
            setUsuario(JSON.parse(usuarioGuardado));
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");
        try {
            const response = await axios.post("http://127.0.0.1:5000/login", { user, password });
            if (response.data.success) {
                const usuarioData = {
                    nombre: response.data.nombre,
                    apellido: response.data.apellido,
                    role: response.data.role
                };
                setUsuario(usuarioData);
                localStorage.setItem("usuario", JSON.stringify(usuarioData));
                setShowLogin(false);
                setUser("");
                setPassword("");
            }
        } catch (err) {
            setError("Usuario o contraseña incorrectos.");
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError("");
        setSuccessMessage("");

        let profileBase64 = null;
        if (profile) {
            const reader = new FileReader();
            reader.readAsDataURL(profile);
            reader.onload = async () => {
                profileBase64 = reader.result.split(",")[1];
                try {
                    const response = await axios.post("http://127.0.0.1:5000/register", {
                        usuario: user,
                        nombre,
                        surname: apellido,
                        email,
                        password,
                        profile: profileBase64
                    });
                    if (response.data.success) {
                        setSuccessMessage("Registro exitoso. Ahora puedes iniciar sesión.");
                        setShowRegister(false);
                        setShowLogin(true);
                        setUser("");
                        setNombre("");
                        setApellido("");
                        setEmail("");
                        setPassword("");
                        setProfile(null);
                    }
                } catch (err) {
                    setError("Error al registrar usuario. Inténtalo de nuevo.");
                }
            };
        }
    };

    const handleLogout = () => {
        setUsuario(null);
        localStorage.removeItem("usuario");
        alert("Has cerrado sesión correctamente.");
    };

    return (
        <>
            <Navbar bg="primary" variant="dark" expand="lg" className="mb-4">
                <Container>
                    <Navbar.Brand href="#home">
                        <img src="/images/logo.png" alt="Logo" className="custom-logo" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="ms-auto">
                            <Nav.Link href="#home">Inicio</Nav.Link>
                            <Nav.Link href="#about">Sobre Nosotros</Nav.Link>
                            <Nav.Link href="#contact">Contacto</Nav.Link>
                            {usuario ? (
                                <NavDropdown title={<><FaUser className="me-1" /> ¡Hola, {usuario.nombre} {usuario.apellido}!</>} id="user-dropdown">
                                    <NavDropdown.Item href="#entradas">
                                        <FaTicketAlt className="me-2" /> Mis Entradas
                                    </NavDropdown.Item>
                                    <NavDropdown.Divider />
                                    <NavDropdown.Item onClick={handleLogout}>
                                        <FaSignOutAlt className="me-2" /> Cerrar Sesión
                                    </NavDropdown.Item>
                                </NavDropdown>
                            ) : (
                                <Nav.Link onClick={() => setShowLogin(true)}>
                                    <FaUser className="me-1" /> Iniciar Sesión
                                </Nav.Link>
                            )}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            {/* Modal de Registro */}
            <Modal show={showRegister} onHide={() => setShowRegister(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Crear Cuenta</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form onSubmit={handleRegister}>
                        <Form.Group className="mb-3">
                            <Form.Label>Usuario</Form.Label>
                            <Form.Control type="text" value={user} onChange={(e) => setUser(e.target.value)} required />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Nombre</Form.Label>
                            <Form.Control type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Apellido</Form.Label>
                            <Form.Control type="text" value={apellido} onChange={(e) => setApellido(e.target.value)} required />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Correo Electrónico</Form.Label>
                            <Form.Control type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contraseña</Form.Label>
                            <Form.Control type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Foto de Perfil</Form.Label>
                            <Form.Control type="file" accept="image/*" onChange={(e) => setProfile(e.target.files[0])} />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="w-100">Registrarse</Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default NavBar;
