import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar } from "react-bootstrap";
import { FaTicketAlt, FaUser } from "react-icons/fa";
import "./NavBar.css";

const NavBar = () => {
    const [usuario, setUsuario] = useState(null);

    // Simulando la autenticación (esto se reemplazaría con datos reales)
    useEffect(() => {
        // Aquí podrías obtener los datos de autenticación desde el backend o localStorage
        const usuarioAutenticado = {
            nombre: "García", // Ejemplo: podrías traerlo de una API
            autenticado: true
        };
        if (usuarioAutenticado.autenticado) {
            setUsuario(usuarioAutenticado);
        }
    }, []);

    return (
        <Navbar bg="primary" variant="dark" expand="lg" className="mb-4">
            <Container>
                <Navbar.Brand href="#home">
                    <img 
                        src="/images/logo.png" 
                        alt="Logo" 
                        className="custom-logo"
                    />
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ms-auto">
                        <Nav.Link href="#home">Inicio</Nav.Link>
                        <Nav.Link href="#about">Sobre Nosotros</Nav.Link>
                        <Nav.Link href="#contact">Contacto</Nav.Link>
                        <Nav.Link href="#entradas">
                            <FaTicketAlt size={22} className="me-2" /> Mis Entradas
                        </Nav.Link>
                        <Nav.Link href="#login">
                            <FaUser size={20} className="me-2" />
                            {usuario ? `Bienvenido, Sr./a ${usuario.nombre}` : "Iniciar Sesión"}
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default NavBar;
