import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";

i18n
  .use(LanguageDetector) // Detecta el idioma del navegador
  .use(initReactI18next) // Inicializa con React
  .init({
    resources: {
      es: {
        translation: {
          "navbar.home": "Inicio",
          "navbar.events": "Eventos",
          "navbar.contact": "Contacto",
          "navbar.login": "Iniciar Sesión",
          "navbar.logout": "Cerrar Sesión",
          "navbar.settings": "Configuración",
          "navbar.language": "Idioma",
          "navbar.hello": "¡Hola, {{name}}!",
          "navbar.myTickets": "Mis Entradas",
          "navbar.admin": "Administrar",
        },
      },
      en: {
        translation: {
          "navbar.home": "Home",
          "navbar.events": "Events",
          "navbar.contact": "Contact",
          "navbar.login": "Login",
          "navbar.logout": "Logout",
          "navbar.settings": "Settings",
          "navbar.language": "Language",
          "navbar.hello": "Hello, {{name}}!",
          "navbar.myTickets": "My Tickets",
          "navbar.admin": "Admin",
        },
      },
      pl: {
        translation: {
          "navbar.home": "Strona Główna",
          "navbar.events": "Wydarzenia",
          "navbar.contact": "Kontakt",
          "navbar.login": "Zaloguj się",
          "navbar.logout": "Wyloguj się",
          "navbar.settings": "Ustawienia",
          "navbar.language": "Język",
          "navbar.hello": "Cześć, {{name}}!",
          "navbar.myTickets": "Moje Bilety",
          "navbar.admin": "Admin",
        },
      },
      ar: {
        translation: {
          "navbar.home": "الرئيسية",
          "navbar.events": "الفعاليات",
          "navbar.contact": "اتصل بنا",
          "navbar.login": "تسجيل الدخول",
          "navbar.logout": "تسجيل الخروج",
          "navbar.settings": "الإعدادات",
          "navbar.language": "اللغة",
          "navbar.hello": "مرحبًا، {{name}}!",
          "navbar.myTickets": "تذاكري",
          "navbar.admin": "الإدارة",
        },
      },
    },
    fallbackLng: "es", // Idioma por defecto
    detection: {
      order: ["localStorage", "navigator"],
      caches: ["localStorage"],
    },
    interpolation: {
      escapeValue: false, // No escapar caracteres HTML
    },
  });

export default i18n;
