import React, { useEffect, useState } from "react";
import { Alert, Button, Container, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Configuracion = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    // Verifica si hay un usuario autenticado
    const user = localStorage.getItem("username");

    useEffect(() => {
        if (!user) {
            navigate("/login"); // Redirigir si no está autenticado
        }
    }, [user, navigate]);

    const handleSaveChanges = async (e) => {
        e.preventDefault();
        setMessage("");
        setError("");

        if (!email || !password) {
            setError("Todos los campos son obligatorios.");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/update-user", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email, password, user }),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(data.message);
            } else {
                throw new Error(data.message || "Error al actualizar los datos.");
            }
        } catch (err) {
            setError(err.message);
        }
    };

    if (!user) {
        return null; // No mostrar nada hasta que redirija
    }

    return (
        <Container className="mt-4">
            <h2>Configuración de Usuario</h2>
            {message && <Alert variant="success">{message}</Alert>}
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSaveChanges}>
                <Form.Group className="mb-3">
                    <Form.Label>Correo Electrónico</Form.Label>
                    <Form.Control 
                        type="email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        placeholder="Introduce tu nuevo correo"
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Nueva Contraseña</Form.Label>
                    <Form.Control 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        placeholder="Introduce tu nueva contraseña"
                    />
                </Form.Group>
                <Button variant="primary" type="submit">Guardar Cambios</Button>
            </Form>
        </Container>
    );
};

export default Configuracion;
