import axios from "axios";
import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { FaCog, FaSignOutAlt, FaTicketAlt, FaUser } from "react-icons/fa";
import "./NavBar.css";

const NavBar = () => {
    const [usuario, setUsuario] = useState(null);
    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);
    const [user, setUser] = useState("");
    const [password, setPassword] = useState("");
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [profile, setProfile] = useState("");
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    useEffect(() => {
        const usuarioGuardado = localStorage.getItem("usuario");
        if (usuarioGuardado) {
            setUsuario(JSON.parse(usuarioGuardado));
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");
        try {
            const response = await axios.post("http://127.0.0.1:5000/login", { user, password });

            if (response.data.success) {
                const usuarioData = {
                    nombre: response.data.nombre,
                    apellido: response.data.apellido,
                    role: response.data.role, // Asegurar que el rol se almacene correctamente
                    profile: response.data.profile || null
                };

                setUsuario(usuarioData);
                localStorage.setItem("usuario", JSON.stringify(usuarioData));
                setShowLogin(false);
                setUser("");
                setPassword("");
                alert("Has iniciado sesión correctamente.");
            } else {
                setError("Usuario o contraseña incorrectos.");
            }
        } catch (err) {
            setError("Error al conectar con el servidor.");
        }
    };

    const handleLogout = () => {
        setUsuario(null);
        localStorage.removeItem("usuario");
        alert("Has cerrado sesión correctamente.");
    };

    return (
        <>
            <Navbar bg="primary" variant="dark" expand="lg" className="mb-4">
                <Container>
                    <Navbar.Brand href="#home">
                        <img src="/images/logo.png" alt="Logo" className="custom-logo" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mx-auto">
                            <Nav.Link href="#home">Inicio</Nav.Link>
                            <Nav.Link href="#about">Sobre Nosotros</Nav.Link>
                            <Nav.Link href="#contact">Contacto</Nav.Link>
                        </Nav>

                        {usuario ? (
                            <Nav className="ms-auto">
                                <NavDropdown
                                    title={
                                        <>
                                            {usuario.profile ? (
                                                <img
                                                    src={usuario.profile}
                                                    alt="Perfil"
                                                    className="profile-image me-2"
                                                />
                                            ) : (
                                                <FaUser className="me-1" />
                                            )}
                                            ¡Hola, {usuario.nombre} {usuario.apellido}!
                                        </>
                                    }
                                    id="user-dropdown"
                                >
                                    {usuario?.role === "estandar" && (
                                        <>
                                            <NavDropdown.Item href="#entradas">
                                                <FaTicketAlt className="me-2" /> Mis Entradas
                                            </NavDropdown.Item>
                                            <NavDropdown.Divider />
                                            <NavDropdown.Item href="#configuracion">
                                                <FaCog className="me-2" /> Configuración
                                            </NavDropdown.Item>
                                        </>
                                    )}

                                    {usuario?.role === "admin" && (
                                        <>
                                            <NavDropdown.Item href="#admin-dashboard">
                                                <FaCog className="me-2" /> Panel de Administrador
                                            </NavDropdown.Item>
                                            <NavDropdown.Item href="#usuarios">
                                                <FaUser className="me-2" /> Gestionar Usuarios
                                            </NavDropdown.Item>
                                            <NavDropdown.Item href="#ventas">
                                                <FaTicketAlt className="me-2" /> Reporte de Ventas
                                            </NavDropdown.Item>
                                        </>
                                    )}

                                    <NavDropdown.Divider />
                                    <NavDropdown.Item onClick={handleLogout}>
                                        <FaSignOutAlt className="me-2" /> Cerrar Sesión
                                    </NavDropdown.Item>
                                </NavDropdown>
                            </Nav>
                        ) : (
                            <Nav className="ms-auto">
                                <Nav.Link onClick={() => setShowLogin(true)}>
                                    <FaUser className="me-1" /> Iniciar Sesión
                                </Nav.Link>
                            </Nav>
                        )}
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </>
    );
};

export default NavBar;
